custom_scss:
/* Variables */
$fprimary: #263238;  // Primary dark color
$fsecondary: #37474f; // Secondary dark color
$ftext: #f5f5f5;       // Text color
$faccent: #4f93ce;      // Accent color

/* Base styles */
body {
  background-color: $fprimary;
  color: $ftext;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
  font-size: 14px;
  margin: 0;
  padding: 0;
}

a {
  color: $faccent;
  text-decoration: none;

  &:hover {
    color: lighten($faccent, 10%);
  }
}

.frappe-control {
  background-color: $fsecondary;
  border: none;
  color: $ftext;
  padding: 10px 15px;
  border-radius: 4px;

  &:focus {
    outline: none;
    background-color: darken($fsecondary, 5%);
  }
}

.frappe-form {
  margin: 20px;
}

.frappe-form-group {
  margin-bottom: 15px;
}

.frappe-label {
  color: $ftext;
  font-weight: bold;
  margin-bottom: 5px;
}

.frappe-button {
  background-color: $faccent;
  color: $fprimary;
  border: none;
  padding: 10px 15px;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: lighten($faccent, 10%);
  }
}

/* Header */
.frappe-header {
  background-color: $fprimary;
  color: $ftext;
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
}

/* Sidebar */
.frappe-sidebar {
  background-color: $fsecondary;
  color: $ftext;
  padding: 10px 0;
}

.frappe-sidebar a {
  color: $ftext;

  &:hover {
    color: $faccent;
  }
}

/* Content Area */
.frappe-container {
  padding: 20px;
}

.frappe-card {
  background-color: $fsecondary;
  color: $ftext;
  border-radius: 4px;
  padding: 15px;
  margin-bottom: 20px;
}

/* Table */
.frappe-table {
  color: $ftext;

  th {
    background-color: $fsecondary;
    padding: 10px 15px;
    border-bottom: 1px solid lighten($fsecondary, 10%);
  }

  td {
    padding: 10px 15px;
    border-bottom: 1px solid lighten($fsecondary, 10%);
  }
}

